mapper.select:

select *
from wc_board
where b_reciver = 'admin'
order by b_regdate desc
limit #{pageSize,javaType=INTEGER}
offset #{startRow,javaType=INTEGER}


select *
from wc_member
where m_id != 'admin'
order by m_regdate desc
limit #{pageSize,javaType=INTEGER}
offset #{startRow,javaType=INTEGER}


select *
from wc_class
where c_confirm = 'N'
order by c_regdate desc
limit #{pageSize,javaType=INTEGER}
offset #{startRow,javaType=INTEGER}


select *
from wc_reply
where b_no is null
order by r_regdate desc
limit #{pageSize,javaType=INTEGER}
offset #{startRow,javaType=INTEGER}


select *
from wc_rsrv_pay
order by p_paydate desc
limit #{pageSize,javaType=INTEGER}
offset #{startRow,javaType=INTEGER}

mapper.update:
update wc_member set
m_pw = #{m_pw}
m_email = #{m_email}
m_name = #{m_name}
m_nick = #{m_nick}
m_regdate = #{m_regdate}
m_phone = #{m_phone}
m_dogtype = #{m_dogtype}
m_dogbirth = #{m_dogbirth}
m_profile = #{m_profile}
m_point = #{m_point}
where m_id = #{m_id}

update wc_class set
c_confirm = 'Y'
where c_no = #{cno}

update wc_reply set
r_content = '관리자에 의해 삭제된 글 입니다'
where r_no = #{rno}
